import styles from "./Input.module.css";
import Button from "./Button";
import { useState } from "react";
import Card from "./Card";
import NewUser from "./NewUser";

const DUMMY_USERS=[];

const Input = (props) => {
  const [username, setUsername] = useState("");
  const [age, setAge] = useState("");
  const [newUser, setNewUser] = useState(DUMMY_USERS);



  const usernameChangeHandler = (event) => {
    setUsername(event.target.value);
  };
  const ageChangeHandler = (event) => {
    setAge(event.target.value);
  };
  const addUserHandler = (event) => {
    event.preventDefault();
    setNewUser({username: username, age: age })
    
   // this.setState({cart: [...this.state.cart, this.state.input]});
    setUsers

    setNewUser(
      <Card>
        <NewUser onNewUser={{ username: username, age: age }} />
      </Card>
    );
  };
  console.log(newUser);
  return (
    <div>
      <Card>
        <form className={styles.form}>
          <label>User Name</label>
          <input
            onChange={usernameChangeHandler}
            value={username}
            className={styles.input}
            type="text"
          />
          <label>Age (years)</label>
          <input
            onChange={ageChangeHandler}
            value={age}
            className={styles.input}
            type="text"
          />
          <Button onClick={addUserHandler} />
        </form>
      </Card>
      {newUser}
    </div>
  );
};
export default Input;
