import styles from "./Input.module.css";
import Button from "./Button";
import { useState } from "react";
import Card from "./Card";
import NewUser from "./NewUser";
import InputValid from "./InputValid";
import EmptyInput from "./emptyInput";
/*

*/
const Input = (props) => {
  const [username, setUsername] = useState("");
  const [age, setAge] = useState("");
  const [newUser, setNewUser] = useState([]);
  const [error, setError] = useState(true);

  const usernameChangeHandler = (event) => {
    setUsername(event.target.value);
  };
  const ageChangeHandler = (event) => {
    setAge(event.target.value);
  };
  const addUserHandler = (event) => {
    event.preventDefault();
    setNewUser((prevUser) => {
      if (age > 0) {
        return [
          ...prevUser,
          {
            username: username,
            age: age,
            key: Math.round(Math.random() * age),
          },
        ];
      } else {
        return [...prevUser];
      }
    });
    setUsername("");
    setAge("");
    if (age < 0) {
      setError(false);
    }
  };

  const errorHandler = () => {
    setError(true);
  };
  console.log(newUser);
  return (
    <div>
      <Card>
        {!error && <InputValid onClick={errorHandler} />}

        <form className={!error ? styles[("form", "filtering")] : "from"}>
          <label>User Name</label>
          <input
            onChange={usernameChangeHandler}
            value={username}
            className={styles.input}
            type="text"
          />
          <label>Age (years)</label>
          <input
            onChange={ageChangeHandler}
            value={age}
            className={styles.input}
            type="text"
          />
          <Button onClick={addUserHandler} />
        </form>
      </Card>
      <div className={!error ? styles["filtering"] : ""}>
        <Card>
          <NewUser onNewUser={newUser} />
        </Card>
      </div>
    </div>
  );
};
export default Input;
